mapper.select:
SELECT category_id, name, created_at, updated_at
FROM categories
WHERE name = #{name}

SELECT category_id, name, created_at, updated_at
FROM categories
WHERE category_id = #{id}

mapper.update:
UPDATE categories
SET name       = #{name},
    updated_at = #{updatedAt}
WHERE category_id = #{categoryId}

mapper.select:
SELECT category_id, name, created_at, updated_at
FROM categories


        category_id > #{cursor}


ORDER BY category_id ASC
LIMIT #{size}
